import React, { useState, useContext, useEffect } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { Form, Button, Row, Col } from "react-bootstrap";
import Message from "../components/Message";
import Loader from "../components/Loader";
import { UserContext } from "../context/UserContext";

const Signup = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [message, setMessage] = useState(null);
  const [passwordValidation, setPasswordValidation] = useState({
    length: false,
    uppercase: false,
    lowercase: false,
    number: false,
    special: false,
  });

  const navigate = useNavigate();
  const location = useLocation();

  const { user, loading, error, register, clearErrors } =
    useContext(UserContext);

  const redirect = location.search ? location.search.split("=")[1] : "/";

  useEffect(() => {
    if (user) {
      navigate(redirect);
    }
  }, [user, navigate, redirect]);

  useEffect(() => {
    // Clear errors when component mounts
    clearErrors();
  }, [clearErrors]); // Include clearErrors in dependency array

  // Password validation function
  const validatePassword = (pwd) => {
    const validation = {
      length: pwd.length >= 6,
      uppercase: /[A-Z]/.test(pwd),
      lowercase: /[a-z]/.test(pwd),
      number: /\d/.test(pwd),
      special: /[!@#$%^&*(),.?":{}|<>]/.test(pwd),
    };
    setPasswordValidation(validation);
    return Object.values(validation).every(Boolean);
  };

  const handlePasswordChange = (e) => {
    const newPassword = e.target.value;
    setPassword(newPassword);
    validatePassword(newPassword);
  };

  const submitHandler = (e) => {
    e.preventDefault();

    if (!validatePassword(password)) {
      setMessage("Password does not meet requirements");
      return;
    }

    if (password !== confirmPassword) {
      setMessage("Passwords do not match");
      return;
    }

    setMessage(null);
    register(name, email, password);
  };

  return (
    <div className="d-flex justify-content-center">
      <div style={{ width: "400px" }}>
        <h1>Sign Up</h1>
        {message && <Message variant="danger">{message}</Message>}
        {error && <Message variant="danger">{error}</Message>}
        {loading && <Loader />}
        <Form onSubmit={submitHandler}>
          <Form.Group className="mb-3" controlId="name">
            <Form.Label>Name</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            ></Form.Control>
          </Form.Group>

          <Form.Group className="mb-3" controlId="email">
            <Form.Label>Email Address</Form.Label>
            <Form.Control
              type="email"
              placeholder="Enter email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            ></Form.Control>
          </Form.Group>

          <Form.Group className="mb-3" controlId="password">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              placeholder="Enter password"
              value={password}
              onChange={handlePasswordChange}
            ></Form.Control>

            {/* Password Requirements */}
            <div className="mt-2">
              <small className="text-muted">Password must contain:</small>
              <ul
                className="list-unstyled mt-1"
                style={{ fontSize: "0.85rem" }}
              >
                <li
                  className={
                    passwordValidation.length ? "text-success" : "text-danger"
                  }
                >
                  {passwordValidation.length ? "✓" : "✗"} At least 6 characters
                </li>
                <li
                  className={
                    passwordValidation.uppercase
                      ? "text-success"
                      : "text-danger"
                  }
                >
                  {passwordValidation.uppercase ? "✓" : "✗"} One uppercase
                  letter (A-Z)
                </li>
                <li
                  className={
                    passwordValidation.lowercase
                      ? "text-success"
                      : "text-danger"
                  }
                >
                  {passwordValidation.lowercase ? "✓" : "✗"} One lowercase
                  letter (a-z)
                </li>
                <li
                  className={
                    passwordValidation.number ? "text-success" : "text-danger"
                  }
                >
                  {passwordValidation.number ? "✓" : "✗"} One number (0-9)
                </li>
                <li
                  className={
                    passwordValidation.special ? "text-success" : "text-danger"
                  }
                >
                  {passwordValidation.special ? "✓" : "✗"} One special character
                  (!@#$%^&*)
                </li>
              </ul>
            </div>
          </Form.Group>

          <Form.Group className="mb-3" controlId="confirmPassword">
            <Form.Label>Confirm Password</Form.Label>
            <Form.Control
              type="password"
              placeholder="Confirm password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
            ></Form.Control>
          </Form.Group>

          <Form.Group className="mb-3" controlId="role">
            <Form.Label>Account Type</Form.Label>
            <Form.Select value={role} onChange={(e) => setRole(e.target.value)}>
              <option value="user">User</option>
              <option value="admin">Admin</option>
            </Form.Select>
            <Form.Text className="text-muted">
              Choose your account type. Admin accounts have additional
              privileges.
            </Form.Text>
          </Form.Group>

          <Button type="submit" variant="primary" className="mb-3">
            Register
          </Button>
        </Form>

        <Row className="py-3">
          <Col>
            Have an Account?{" "}
            <Link to={redirect ? `/login?redirect=${redirect}` : "/login"}>
              Login
            </Link>
          </Col>
        </Row>
      </div>
    </div>
  );
};

export default Signup;
