import React, { useState, useEffect } from 'react';
import { Row, Col, Card } from 'react-bootstrap';
import Message from '../components/Message';
import Loader from '../components/Loader';
import { orderAPI } from '../api/orderAPI';
import { productAPI } from '../api/productAPI';
import { userAPI } from '../api/userAPI';

const AdminDashboard = () => {
  const [stats, setStats] = useState({
    totalOrders: 0,
    totalProducts: 0,
    totalUsers: 0,
    totalRevenue: 0,
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        setLoading(true);

        // Fetch all data
        const [orders, products, users] = await Promise.all([
          orderAPI.getAllOrders(),
          productAPI.getAllProducts(),
          userAPI.getAllUsers(),
        ]);

        // Calculate stats
        const totalRevenue = orders.reduce((acc, order) => 
          order.isPaid ? acc + order.totalPrice : acc, 0
        );

        setStats({
          totalOrders: orders.length,
          totalProducts: products.length,
          totalUsers: users.length,
          totalRevenue: totalRevenue.toFixed(2),
        });
      } catch (error) {
        setError(error.response?.data?.message || error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  if (loading) return <Loader />;
  if (error) return <Message variant="danger">{error}</Message>;

  return (
    <>
      <h1>Admin Dashboard</h1>
      <Row>
        <Col md={3}>
          <Card className="text-white bg-primary mb-3">
            <Card.Header>Total Orders</Card.Header>
            <Card.Body>
              <Card.Title>{stats.totalOrders}</Card.Title>
            </Card.Body>
          </Card>
        </Col>
        <Col md={3}>
          <Card className="text-white bg-success mb-3">
            <Card.Header>Total Products</Card.Header>
            <Card.Body>
              <Card.Title>{stats.totalProducts}</Card.Title>
            </Card.Body>
          </Card>
        </Col>
        <Col md={3}>
          <Card className="text-white bg-info mb-3">
            <Card.Header>Total Users</Card.Header>
            <Card.Body>
              <Card.Title>{stats.totalUsers}</Card.Title>
            </Card.Body>
          </Card>
        </Col>
        <Col md={3}>
          <Card className="text-white bg-warning mb-3">
            <Card.Header>Total Revenue</Card.Header>
            <Card.Body>
              <Card.Title>${stats.totalRevenue}</Card.Title>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </>
  );
};

export default AdminDashboard;
